// const buildPage = (curPage) => {
//   const trimEnd = curPage + numberPerPage - 1;
//   console.log(counts.slice(curPage - 1, trimEnd))
// }
// buildPage(2);

const ReactPagination = (props) => {
    const { 
        theme, totalSize, sizePerPage, currentPage, showFirstLastPages,
        firstPageText, lastPageText, previousPageText, nextPageText
    } = props;
    let pageNum = Math.ceil(totalSize / sizePerPage);
  }


class Pager extends Component {
  render() {
    return (
      // <div className="pagers">
      //   <a href=".">&laquo;</a>
      //   <a href=".">1</a>
      //   <a href=".">2</a>
      //   <a href=".">3</a>
      //   <a href=".">4</a>
      //   <a href=".">5</a>
      //   <a href=".">6</a>
      //   <a href=".">&raquo;</a>
      // </div>
      <div className={`react-pagination-js-${theme}`}>
        <ul>
          {showFirstLastPages ? (
            <Page
              {...props}
              className={currentPage === 1 ? "page disabled" : "page"}
              label={firstPageText}
              pageNum={1}
            />
          ) : null}
          <Page
            {...props}
            className={currentPage === 1 ? "page disabled" : "page"}
            label={previousPageText}
            pageNum={currentPage - 1}
          />
          <Pagination {...props} pageNum={pageNum} />
          <Page
            {...props}
            className={currentPage === pageNum ? "page disabled" : "page"}
            label={nextPageText}
            pageNum={currentPage + 1}
          />
          {showFirstLastPages ? (
            <Page
              {...props}
              className={currentPage === pageNum ? "page disabled" : "page"}
              label={lastPageText}
              pageNum={pageNum}
            />
          ) : null}
        </ul>
      </div>
    );
  }
}

export default Pager;
